function formatFert(data) {
    // Sort the fertilizer data based on position and format into HTML paragraphs
    return Object.entries(data)
        .sort((a, b) => a[1].position - b[1].position)
        .map(([key, value]) => {
            const formattedKey = key.replace(/_/g, ' ').toUpperCase(); // Convert key to uppercase and replace underscores with spaces
            return `<p>${formattedKey}: ${value.concentration}ml/L (${value.total}ml total)</p>`;
        })
        .join('\n'); // Join all paragraphs with newline characters
}

function generateHTML() {
    // Retrieve total water and pH values from global storage
    const totalWater = `<p>TOTAL WATER: ${global.get("water")}L </p>`;
    const ph = `<p>PH: ${global.get("ph")}</p>`;

    // Format fertilizer data
    const formattedFert = formatFert(global.get("fert"));

    // Retrieve notes from global storage, only include if defined
    const notes = global.get("notes") !== undefined ? `<p>NOTES: <br/>${global.get("notes")}</p>` : "";

    // Construct and return the complete HTML structure
    return `<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
</head>
<body>
${totalWater}
${ph}
${formattedFert}
${notes}
</body>
</html>`;
}

